tdesr_read.etu <- function() {
  etu <- read.table("fr-esr-statistiques-sur-les-effectifs-d-etudiants-inscrits-par-etablissement.csv",
                    header=TRUE, sep=';', quote='"') %>%
    transmute(
      UAI = ETABLISSEMENT,
      Rentrée = as.factor(RENTREE),
      pki.ETU.P.effectif = Nombre.d.étudiants.inscrits..inscriptions.principales..hors.étudiants.inscrits.en.parallèle.en.CPGE,
      pki.ETU.S.cycle.1.L = Cycle.universitaire..cursus.LMD....L..1er.cycle.-(Nombre.d.étudiants.inscrits..inscriptions.principales.-pki.ETU.P.effectif),
      pki.ETU.S.cycle.2.M = Cycle.universitaire..cursus.LMD....M..2ème.cycle.,
      pki.ETU.S.cycle.3.D = Cycle.universitaire..cursus.LMD....D..3ème.cycle.,
      #pki.ETU.S.diplome.national = Type.de.diplôme...Diplômes.nationaux,
      pki.ETU.S.diplomeEtablissement = Type.de.diplôme...Diplômes.d.établissement
  )
}
